(function(){let t=0,n="";const e=document.querySelector(".header"),s=document.querySelector(".float-menu");window.addEventListener("scroll",o=>{if(window.scrollY==0){e.classList.remove("hide"),s.classList.remove("show");return}s.classList.add("show");const a=window.scrollY-t,i=a>0?"DOWN":"UP";i!=n&&(i=="DOWN"?e.classList.add("hide"):e.classList.remove("hide"),n=i),t=window.scrollY})})()