(function(){let n=0,s="";const e=document.querySelector(".header"),t=document.querySelector(".float-menu");window.addEventListener("scroll",o=>{window.scrollY==0&&(e.classList.remove("hide"),t.classList.remove("show"));const a=window.scrollY-n,i=a>0?"DOWN":"UP";i!=s&&(i=="DOWN"?(e.classList.add("hide"),t.classList.add("show")):(e.classList.remove("hide"),t.classList.remove("show")),s=i),n=window.scrollY})})()