function isSystemPreferDarkTheme(){const e=window.matchMedia("(prefers-color-scheme: dark)");return e.matches}let $html=document.querySelector("html");const theme=sessionStorage.getItem("theme");switch(theme){case"light":$html.classList.remove("dark-theme");break;case"dark":$html.classList.add("dark-theme");break;case"system":const e=isSystemPreferDarkTheme();e?$html.classList.add("dark-theme"):$html.classList.remove("dark-theme");break}