const sideToc=document.querySelector(".side-toc__child"),sideTocItems=sideToc.querySelectorAll("nav > ul > li");function hightlightActiveHeading(){const e=document.querySelectorAll("article h2"),t=new IntersectionObserver(e=>{e.forEach(e=>{if(e.isIntersecting){const t=e.target.id;sideTocItems.forEach(e=>{e.classList.remove("active");const n=e.querySelector("a");n.getAttribute("href")==`#${t}`&&e.classList.add("active")})}})},{root:null,rootMargin:"0px 0px -50% 0px",threshold:.1});e.forEach(e=>t.observe(e))}hightlightActiveHeading()